    private void buton_TemizleActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_buton_TemizleActionPerformed
        label_takımadı.setText("");
        label_ligAdı.setText("");
        sp_oynanan.setValue(0); //spinnerdeki değeri aldık
        sp_win.setValue(0);
        sp_drawn.setValue(0);
        sp_lost.setValue(0);
        sp_atılan.setValue(0);
        sp_yenen.setValue(0);
        textf_averaj.setText("");
        textf_puan.setText("");
    }//GEN-LAST:event_buton_TemizleActionPerformed

    //textfieldlere yazdığımız bilgileri getText() ile alıp veritabanına yazıyoruz.
    private void buton_EkleActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_buton_EkleActionPerformed

        String takımAdı = label_takımadı.getText();
        String ligAdı = label_ligAdı.getText();
        int oynanan =(Integer) sp_oynanan.getValue(); //spinnerdeki değeri aldık
        int win = (Integer)sp_win.getValue();
        int drawn =(Integer) sp_drawn.getValue();
        int lost =(Integer) sp_lost.getValue();
        int atılan =(Integer) sp_atılan.getValue();
        int yenen =(Integer) sp_yenen.getValue();
        int lig_id = 0;

        //lig adı için

        

        //averaj için
        int averaj = atılan - yenen;
        textf_averaj.setText(String.valueOf(averaj)); //yazdırmak için stringe çevirdik

        //toplam puan için
        int toplamPuan = win * 3 + drawn;
        textf_puan.setText(String.valueOf(toplamPuan));

        if (boşlukKontrol() && oyunSayısıKontrol() && sayıDeğeriKontrol()){
            //veritabanına ekleye
            Puanlar tk = new Puanlar();
            tk.EkleGüncelleSilTakım('e', null, takımAdı, ligAdı, oynanan, win, drawn, lost,
                atılan, yenen, averaj, toplamPuan); //ekleme yapıldı
        }
        else{ //birden çok hata varsa uyarıların hepsinin sırayla alınmasını istedim.
            if(!boşlukKontrol()){
                JOptionPane.showMessageDialog(null, "takım ismi veya oyun sayısı geçerli değil!");
            }

            if (!oyunSayısıKontrol()){
                JOptionPane.showMessageDialog(null, "oynanan oyun sayısı ile diğer veriler eşleşmedi!");
            }
            if (!sayıDeğeriKontrol()){
                JOptionPane.showMessageDialog(null, "Girilen sayı değerleri sıfırdan küçük olamaz!");
            }
        }

        //eski tablodan bir tane daha eklemek yerine üstüne ekler
        PuanTablosu.PuanTablosu.setModel(new DefaultTableModel(null, new Object[] {"takım_id", "takım adı", "lig adı", "oynanan maç", "galibiyet", "beraberlik", "mağlubiyet", "atılan gol", "yenilen gol", "averaj", "toplam puan"}));

        //puan tablosuna ekleme yapılacağında güncel olarak yenilenmesini sağlar
        puant.fillPuanTablosu(PuanTablosu.PuanTablosu, "");

    }//GEN-LAST:event_buton_EkleActionPerformed

