    //takım adı ve oynanan oyun sayıları boşsa 0 değeri dönecek ve ekleme yapılamayacak
    public boolean boşlukKontrol(){
        return !(label_takımadı).getText().equals("") && !((Integer)sp_oynanan.getValue()).equals(0); 
    }   
    
    //galibiyet beraberlik ve mağlubiyet sayılarının toplamı oynanan oyun sayısını tutmuyorsa da çalışmayacak
    public boolean oyunSayısıKontrol(){
        int toplamOyun = (Integer)sp_win.getValue() + (Integer)sp_drawn.getValue() + (Integer)sp_lost.getValue();
        int kontrol = (Integer) sp_oynanan.getValue();
        return (toplamOyun == kontrol);
        
    }
    
    //sayı değerlerinin pozitif veya 0 olduğunun kontrolünü sağlar
    public boolean sayıDeğeriKontrol(){
        return ((Integer) sp_oynanan.getValue() >= 0 && (Integer)sp_win.getValue()>= 0 && (Integer)sp_drawn.getValue()>= 0 && 
                (Integer)sp_lost.getValue()>= 0 && (Integer) sp_atılan.getValue()>= 0 && (Integer) sp_yenen.getValue()>= 0);
    }
